package programs;

public class even {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			int no=10;
			for(int i=1;i<=no;i++)
			{
				if(i%2==0)
				{
					System.out.println(i+"is"+"even number");
				}
				else {
					System.out.println(i+"is"+"not a even number");
				}
			}
			System.out.println("i am te1");
	}

}
